import React, { Component } from "react";
import { Tabs, Icon, Menu } from "antd";
import 'antd/dist/antd.css';

import UserProfile from "./UserProfile/UserProfile";
import Conversations from "./Conversations/Conversations";
import Groups from "./Groups/Groups";


const TabPane = Tabs.TabPane;
const { SubMenu }  = Menu;


export class Dashboard extends Component {

  handleClick = e => {
    console.log('click ', e);
  };

  render() {
    const tabs = [
      { key: 1, title: "Profile", icon: "user", component: UserProfile },
      { key: 2, title: "Conversation", icon: "message", component: Conversations },
      { key: 3, title: "Groups", icon: "team", component: Groups }
    ];
    // const tabsContent = tabs.map(tab => {
      return (
          <Menu
              onClick={this.handleClick}
              style={{ width: 220, height: '100vh' }}
              defaultSelectedKeys={['1']}
              mode="inline"
          >
            {tabs.map(tab => (
              <SubMenu
                  key={tab.key}
                  // onClick={}
                  title={
                    <div style={{ display: 'flex', justifyContent: 'left' }}>
                    <span>
                      <Icon type={tab.icon} />
                      <span>{tab.title}</span>
                    </span>
                    </div>
                  }
              />
            ))}
          </Menu>
        // <TabPane
        //   tab={
        //     <span>
        //       <Icon type={tab.icon} />
        //       {tab.title}
        //     </span>
        //   }
        //   key={tab.key}
        // >
        //   <tab.component />
        // </TabPane>
      );
    // });
    // return <Tabs defaultActiveKey="1">{tabsContent}</Tabs>;
  }
}

export default Dashboard;
